D ngay <= '2023-03-31' THEN DATEADD(DAY, @ngay_otc_cu, ngay) WHEN PHANLOAI <> 'OTC' AND ngay > '2023-03-31' THEN DATEADD(DAY, @ngay_etc, ngay) WHEN PHANLOAI <> 'OTC' AND ngay <= '2023-03-31' THEN DATEADD(DAY, @ngay_etc_cu, ngay) END WHERE ngay > '2023-03-31' OR ngay <= '2023-03-31' UPDATE @TBL_IN SET NGAYDENHAN=DATEADD(DAY, 60, ngay) WHERE MAKH1 in ('50CT90') UPDATE @TBL_IN SET NGAYDENHAN=DATEADD(DAY, 90, ngay) WHERE MAKH1 in ('78CT13') UPDATE @TBL_IN SET NGAYDENHAN=DATEADD(DAY, 150, ngay) WHERE MAKH1 in ('78CT13_E') UPDATE @TBL_IN SET songay= DATEDIFF(dd, NGAYDENHAN, '" + ngaycuoithangtruoc.ToString("MM/dd/yyyy") + "') UPDATE @TBL_IN SET D_0_6=tien WHERE songay <=0 AND songay>=-6 UPDATE @TBL_IN SET D_7_14=tien WHERE songay<-6 AND songay>=-14 UPDATE @TBL_IN SET D_15_30=tien WHERE songay<-14 AND songay>=-30 UPDATE @TBL_IN SET D_31_60=tien WHERE songay<-30 AND songay>=-60 UPDATE @TBL_IN SET D_61_90=tien WHERE songay<-60 AND songay>=-90 UPDATE @TBL_IN SET D_91_120=tien WHERE songay<-90 AND songay>=-120 UPDATE @TBL_IN SET D_121_180=tien WHERE songay<-120 AND songay>=-180 UPDATE @TBL_IN SET D_OVER_180=tien WHERE songay<-180 UPDATE @TBL_IN SET QUAHAN_songay=DATEDIFF (dd, NGAYDENHAN, '" + ngaycuoithangtruoc.ToString("MM/dd/yyyy") + "') UPDATE @TBL_IN SET QUAHAN_1_6=tien WHERE QUAHAN_songay>0 AND QUAHAN_songay<=6 UPDATE @TBL_IN SET QUAHAN_7_14=tien WHERE QUAHAN_songay>6 AND QUAHAN_songay<=14 UPDATE @TBL_IN SET QUAHAN_15_30=tien WHERE QUAHAN_songay>14 AND QUAHAN_songay<=30 UPDATE @TBL_IN SET QUAHAN_31_60=tien WHERE QUAHAN_songay>30 AND QUAHAN_songay<=60 UPDATE @TBL_IN SET QUAHAN_61_90=tien WHERE QUAHAN_songay>60 AND QUAHAN_songay<=90 UPDATE @TBL_IN SET QUAHAN_91_120=tien WHERE QUAHAN_songay>90 AND QUAHAN_songay<=120 UPDATE @TBL_IN SET QUAHAN_121_180=tien WHERE QUAHAN_songay>120 AND QUAHAN_songay<=180 UPDATE @TBL_IN SET QUAHAN_181_360=tien WHERE QUAHAN_songay>180 AND QUAHAN_songay<=360 UPDATE @TBL_IN SET QUAHAN_361_720=tien WHERE QUAHAN_songay>360 AND QUAHAN_songay<=720 UPDAT