        [ActionName("doanh-so-theo-thang")]
        [Authorize(Roles = "CAP1,CAP2,ADMIN")]
        [HttpPost]
        [OutputCache(VaryByParam = "*", Duration = 0, NoStore = true)]
        public ActionResult Index(string tungay1, string denngay1, int sltopkhuvuc, int sltopkhachhang, int sltophanghoa, List<string> nhomhang, string maphanloai, List<string> Checkboxlist1, string Checkboxlist2, List<string> Checkboxlist3, List<string> Checkboxlist4, List<string> Checkboxlist5)
        {


            DateTime tungay = DateTime.ParseExact(tungay1, "dd/MM/yyyy", CultureInfo.InvariantCulture);

            DateTime denngay = DateTime.ParseExact(denngay1, "dd/MM/yyyy", CultureInfo.InvariantCulture);
            var data = new Datachart();
            if (Checkboxlist4 == null)
            {
                if (nhomhang.Contains("ALL"))
                {
                    Checkboxlist4 = null;
                }
                else
                {
                    Checkboxlist4 = string.Join(",", nhomhang).Split(',').ToList();
                }
            }
            //PL KHACH HANG
            var listcheckbox1 = String.Join(",", Checkboxlist1.ToArray()).Split(',').Distinct().ToList();
            Checkboxlist1 = db2.TBL_DANHMUCNGUOIDUNG.SingleOrDefault(n => n.nguoidung == User.Identity.Name).macn.Split(',').Intersect(listcheckbox1).ToList();
            JsonSerializerSettings jsonSetting = new JsonSerializerSettings() { NullValueHandling = NullValueHandling.Ignore };

            var pickthang = DATAALL(Checkboxlist1, tungay.ToString("yyyy-MM-dd"), denngay.ToString("yyyy-MM-dd"), Checkboxlist4).ToList();
            //MAPL
            //LỌC OTC/ETC
            if (Checkboxlist2 != "null")
            {
                pickthang = pickthang.Where(n => Checkboxlist2.Contains(n.PhanLoai)).Distinct().ToList();
            }
            if (Checkboxlist3.Contains("null") == false)
            {
                pickthang = pickthang.Where(n => Checkboxlist3.Contains(n.PhanLoaiKH)).Distinct().ToList();
            }
            if (Checkboxlist5 != null)
            {
                pickthang = pickthang.Where(n => Checkboxlist5.Contains(n.MaHH)).Distinct().ToList();
            }
            //
            if (maphanloai != "null")
            {
                var flag = 0;
                if (maphanloai == "BAN,XK")
                {
                    flag = 1;
                }
                if (flag == 0)
                {
                    var dulieu = pickthang.Where(n => n.MaPL == maphanloai).ToList();
                    var xyz = db2.DANHMUCPHANLOAIHDs.SingleOrDefault(n => n.MAPK == maphanloai);
                    var dulieu1 = pickthang.Where(n => n.MaPL == xyz.TKHN).ToList();
                    pickthang = dulieu.Concat(dulieu1).Distinct().ToList();
                }
            }
            if (!pickthang.Any())
            {
                return Json(data);
            }
            //CHART1
            var doanhthuchinhanh = pickthang.GroupBy(n => n.Tendvbc).Select(cl => new DoanhThuCN { Tendvbc = cl.First().Tendvbc, TongTien = cl.Sum(c => c.Tien) }).OrderBy(n => n.TongTien);
            decimal TONG = doanhthuchinhanh.Sum(n => n.TongTien);
            var sodem2 = doanhthuchinhanh.Count();
            List<DataPoint> dataPoints = new List<DataPoint>();
            foreach (var i in doanhthuchinhanh)
            {
                dataPoints.Add(new DataPoint((double)i.TongTien / 1000000000, sodem2 + " . " + i.Tendvbc, Math.Round((double)i.TongTien / 1000000000, 2).ToString() + " (" + Math.Round((double)(i.TongTien * 100 / TONG), 2).ToString() + "%)"));
                sodem2--;
            }
            dataPoints.Add(new DataPoint((double)TONG / 1000000000, GlobalRes.lbtongdoanhthu, Math.Round((double)TONG / 1000000000, 2).ToString() + " (100%)"));
            data.Chart1Height = 150 + dataPoints.Count() * 30;
            data.DataPoints = JsonConvert.SerializeObject(dataPoints, jsonSetting);
            //
            //CHART2
            var doanhthuhangthang = pickthang.GroupBy(n => n.Thang).Select(cl => new DoanhHangThang { thang = cl.First().Thang, TongTien = cl.Sum(c => c.Tien) }).OrderBy(n => n.thang).ToList();
            List<DataPoint> dataPoints1 = new List<DataPoint>();
            foreach (var x in doanhthuhangthang)
            {
                dataPoints1.Add(new DataPoint(x.thang, (double)x.TongTien / 1000000000, Math.Round((double)x.TongTien / 1000000000, 2).ToString() + " (" + Math.Round((double)(x.TongTien * 100 / TONG), 2).ToString() + "%)"));
            }
            data.DataPoints1 = JsonConvert.SerializeObject(dataPoints1, jsonSetting);
            //
            //CHART3
            List<DataPoint> dataPoints2 = new List<DataPoint>();
            var doanhthuvungmien = pickthang.GroupBy(n => n.Mien).Select(cl => new DoanhThuCN { Tendvbc = cl.First().Mien, TongTien = cl.Sum(c => c.Tien) }).OrderByDescending(n => n.TongTien);
            foreach (var i in doanhthuvungmien)
            {
                if (Thread.CurrentThread.CurrentCulture.Name == "vi")
                {
                    dataPoints2.Add(new DataPoint((double)i.TongTien / 1000000000, i.Tendvbc));
                }
                else
                {
                    if (i.Tendvbc == "MIỀN TRUNG")
                    {
                        dataPoints2.Add(new DataPoint((double)i.TongTien / 1000000000, "CENTRAL"));
                    }
                    else if (i.Tendvbc == "MIỀN NAM")
                    {
                        dataPoints2.Add(new DataPoint((double)i.TongTien / 1000000000, "SOUTH"));
                    }
                    else if (i.Tendvbc == "MIỀN BẮC")
                    {
                        dataPoints2.Add(new DataPoint((double)i.TongTien / 1000000000, "NORTHERN"));
