            List<DTA_TONKHO_NHAP> duplicates = data.GroupBy(x => new { x.mahh, x.malo, x.kho, x.trangthai }).SelectMany(g => g.Skip(1)).ToList();
            var errortext = "Các mã hàng hóa , số lô sau có trên 1 hàng trong file excel (Format Mã Kho - Mã hàng hóa- Số lô - trạng thái) :";
            foreach (var i in duplicates)
            {
                errortext = errortext + "[" + i.mahh + " - " + i.malo + "]";
            }
            if (duplicates.Count != 0)
            {
                errortext = errortext + ". Vui lòng kiểm tra lại, hoặc gộp lại.";
                return Json(errortext);
            }
            var dem = 0;
            foreach (var i in data)
            {
                try
                {
                    var ngayhamdung = DateTime.ParseExact(i.handung, "ddMMyy", CultureInfo.InvariantCulture);
                }
                catch
                {
                    dem = dem + 1;
                    errortext = errortext + "[" + i.mahh + " - " + i.malo + "]";
                }
            }
            if (dem != 0)
            {
                errortext = errortext + ". Vui lòng kiểm tra lại, hạn dùng không đúng format ddMMyy.";
                return Json(errortext);
            }
            var thang = data.First().thang;
            var nam = data.First().nam;
            if (queryCN.SingleOrDefault(n => n.macn == data.First().mach) != null)
            {
                queryCN.SingleOrDefault(n => n.macn == data.First().mach).data.DTA_TONKHO_NHAP.AddRange(data);
                queryCN.SingleOrDefault(n => n.macn == data.First().mach).data.SaveChanges();
            }
            else if (queryCH.SingleOrDefault(n => n.macn == data.First().mach) != null)
            {
                queryCH.SingleOrDefault(n => n.macn == data.First().mach).data.DTA_TONKHO_NHAP.AddRange(data);
                queryCH.SingleOrDefault(n => n.macn == data.First().mach).data.SaveChanges();
            }
            return Json("1");
        }
