        [HttpPost]
        [Authorize(Roles = "NHAPKHOANTDV")]
        public ActionResult Themmatdv(List<Themmatdv> data)
        {
            List<Themmatdv> duplicates = data.Distinct().ToList();
            var listcn = data.Select(n => n.macn).Distinct().ToList();
            try
            {
                foreach (var i in listcn)
                {
                    if (queryCN.SingleOrDefault(n => n.macn == i) != null)
                    {
                        var matdvthem = new List<TBL_DANHMUCTDV>();
                        foreach (var z in duplicates.Where(n => n.macn == i))
                        {
                            if (z.idglobal_region != "" && z.idglobal_region != null)
                            {
                                matdvthem.Add(new TBL_DANHMUCTDV() { MaTDV = z.idglobal_region, TenTDV = z.ten_region, idglobal = z.idglobal_region });
                            }
                            if (z.idglobal_manager != "" && z.idglobal_manager != null)
                            {
                                matdvthem.Add(new TBL_DANHMUCTDV() { MaTDV = z.idglobal_manager, TenTDV = z.ten_manager, idglobal = z.idglobal_manager });
                            }
                            if (z.idglobal_director != "" && z.idglobal_director != null)
                            {
                                matdvthem.Add(new TBL_DANHMUCTDV() { MaTDV = z.idglobal_director, TenTDV = z.ten_director, idglobal = z.idglobal_director });
                            }
                        }
                        matdvthem = matdvthem.GroupBy(n => n.MaTDV).Select(n => n.FirstOrDefault()).ToList();
                        var matdv = queryCN.SingleOrDefault(n => n.macn == i).data.TBL_DANHMUCTDV.Select(n => n.idglobal).Distinct().ToList();
                        queryCN.SingleOrDefault(n => n.macn == i).data.TBL_DANHMUCTDV.AddRange(matdvthem.Where(n => !matdv.Contains(n.idglobal)));
                        queryCN.SingleOrDefault(n => n.macn == i).data.SaveChanges();
                    }
                    else if (queryCH.SingleOrDefault(n => n.macn == i) != null)
                    {
                        var matdvthem = new List<DS_TDV_PTTT>();
                        foreach (var z in duplicates.Where(n => n.macn == i))
                        {
                            if (z.idglobal_region != "" && z.idglobal_region != null)
                            {
                                matdvthem.Add(new DS_TDV_PTTT() { MaTDV = z.idglobal_region, TenTDV = z.ten_region, idglobal = z.idglobal_region });
                            }
                            if (z.idglobal_manager != "" && z.idglobal_manager != null)
                            {
                                matdvthem.Add(new DS_TDV_PTTT() { MaTDV = z.idglobal_manager, TenTDV = z.ten_manager, idglobal = z.idglobal_manager });
                            }
                            if (z.idglobal_director != "" && z.idglobal_director != null)
                            {
                                matdvthem.Add(new DS_TDV_PTTT() { MaTDV = z.idglobal_director, TenTDV = z.ten_director, idglobal = z.idglobal_director });
                            }
                        }
                        matdvthem = matdvthem.Distinct().ToList();
                        var matdv = queryCH.SingleOrDefault(n => n.macn == i).data.DS_TDV_PTTT.Select(n => n.idglobal).Distinct().ToList();
                        queryCH.SingleOrDefault(n => n.macn == i).data.DS_TDV_PTTT.AddRange(matdvthem.Where(n => !matdv.Contains(n.MaTDV)));
                        queryCH.SingleOrDefault(n => n.macn == i).data.SaveChanges();
                    }
                }
            }
            catch (Exception ex)
            {
                return Json("1");
            }

            return Json("1");
        }
        public List<string> ContainsAllItems(List<string> a, List<string> b)
        {
            var check = b;
            foreach (var i in b)
            {
                if (a.Contains(i))
                {
                    check.Remove(i);
                }
            }
            return check;
        }
