        [ActionName("so-sanh-theo-nam")]
        [Authorize(Roles = "CAP1,CAP2,ADMIN")]
        [HttpPost]
        public ActionResult SoSanh(int tuthang, int denthang, int nam, List<string> nhomhang, string maphanloai, List<string> Checkboxlist1, string Checkboxlist2, List<string> Checkboxlist3, List<string> Checkboxlist4, List<string> Checkboxlist5)
        {
            //PL KHACH HANG
            var datafinal = new Datachartss();
            if (Checkboxlist4 == null)
            {
                if (nhomhang.Contains("ALL"))
                {
                    Checkboxlist4 = null;
                }
                else
                {
                    Checkboxlist4 = string.Join(",", nhomhang).Split(',').ToList();
                }
            }
            JsonSerializerSettings jsonSetting = new JsonSerializerSettings() { NullValueHandling = NullValueHandling.Ignore };
            var pickthang = DATAALL(Checkboxlist1, GetFirstDay(tuthang, nam - 1).ToString("yyyy-MM-dd"), GetLastDay(denthang, nam - 1).ToString("yyyy-MM-dd"), Checkboxlist4);
            var pickthang1 = DATAALL(Checkboxlist1, GetFirstDay(tuthang, nam).ToString("yyyy-MM-dd"), GetLastDay(denthang, nam).ToString("yyyy-MM-dd"), Checkboxlist4);
            //LỌC OTC/ETC
            if (Checkboxlist2 != "null")
            {
                pickthang = pickthang.Where(n => Checkboxlist2.Contains(n.PhanLoai)).ToList();
                pickthang1 = pickthang1.Where(n => Checkboxlist2.Contains(n.PhanLoai)).ToList();
            }
            if (Checkboxlist3.Contains("null") == false)
            {
                pickthang = pickthang.Where(n => Checkboxlist3.Contains(n.PhanLoaiKH)).ToList();
                pickthang1 = pickthang1.Where(n => Checkboxlist3.Contains(n.PhanLoaiKH)).ToList();
            }

            //
            if (maphanloai != "null")
            {
                var flag = 0;
                if (maphanloai == "BAN,XK")
                {
                    flag = 1;
                }
                if (flag == 0)
                {
                    var dulieu = pickthang.Where(n => n.MaPL == maphanloai).ToList();
                    var xyz = db2.DANHMUCPHANLOAIHDs.SingleOrDefault(n => n.MAPK == maphanloai);
                    var dulieu1 = pickthang.Where(n => n.MaPL == xyz.TKHN).ToList();
                    pickthang = dulieu.Concat(dulieu1).ToList();
                    var dulieu2 = pickthang1.Where(n => n.MaPL == maphanloai).ToList();
                    var dulieu3 = pickthang1.Where(n => n.MaPL == xyz.TKHN).ToList();
                    pickthang1 = dulieu2.Concat(dulieu3).ToList();
                }
            }
            //
            if (!pickthang.Any() || !pickthang1.Any())
            {
                return Json("0");
            }
            //CHART1
            var doanhthuchinhanh = pickthang.GroupBy(n => n.Tendvbc).Select(cl => new DoanhThuCN { Tendvbc = cl.First().Tendvbc, TongTien = cl.Sum(c => c.Tien) }).OrderBy(n => n.Tendvbc).ToList();
            var doanhthuchinhanh1 = pickthang1.GroupBy(n => n.Tendvbc).Select(cl => new DoanhThuCN { Tendvbc = cl.First().Tendvbc, TongTien = cl.Sum(c => c.Tien) }).OrderBy(n => n.Tendvbc).ToList();
            //DEMO

            foreach (var i in doanhthuchinhanh)
            {
                if (doanhthuchinhanh1.SingleOrDefault(n => n.Tendvbc == i.Tendvbc) == null)
                {
                    doanhthuchinhanh1.Add(new DoanhThuCN { Tendvbc = i.Tendvbc, TongTien = 0 });
                }
            }
            foreach (var i in doanhthuchinhanh1)
            {
                if (doanhthuchinhanh.SingleOrDefault(n => n.Tendvbc == i.Tendvbc) == null)
                {
                    doanhthuchinhanh.Add(new DoanhThuCN { Tendvbc = i.Tendvbc, TongTien = 0 });
                }
            }
            var chart1data = new List<TopKhuVuc>();
            foreach (var i in doanhthuchinhanh1.OrderBy(n => n.TongTien))
            {
                var datay = doanhthuchinhanh.SingleOrDefault(n => n.Tendvbc == i.Tendvbc);
                chart1data.Add(new TopKhuVuc { Ten = i.Tendvbc, Nam = i.TongTien, Namtruoc = datay.TongTien });
            }
            decimal CHART1TONG = chart1data.Sum(n => n.Namtruoc);
            decimal CHART1TONG1 = chart1data.Sum(n => n.Nam);

            // Năm 1
            List<DataPoint> dataPoints = new List<DataPoint>();
            var sodem = chart1data.Count();
            foreach (var i in chart1data)
            {
                dataPoints.Add(new DataPoint((double)i.Namtruoc / 1000000000, sodem + " . " + i.Ten));
                sodem--;
            }
            dataPoints.Add(new DataPoint((double)CHART1TONG / 1000000000, GlobalRes.lbtongdoanhthu));
            datafinal.DataPoints = JsonConvert.SerializeObject(dataPoints, jsonSetting);
            // Năm 2
            List<DataPoint> dataPoints0 = new List<DataPoint>();
            sodem = chart1data.Count();
            foreach (var i in chart1data)
            {
                dataPoints0.Add(new DataPoint((double)i.Nam / 1000000000, sodem + " . " + i.Ten));
                sodem--;
            }
            dataPoints0.Add(new DataPoint((double)CHART1TONG1 / 1000000000, GlobalRes.lbtongdoanhthu));
            datafinal.DataPoints0 = JsonConvert.SerializeObject(dataPoints0, jsonSetting);
            // Tỉ lệ
            List<DataPoint> dataPoints00 = new List<DataPoint>();
            for (int i = 0; i < dataPoints.Count; i++)
            {
                if (dataPoints0[i].Y == 0)
                {
                    dataPoints00.Add(new DataPoint(-100, dataPoints[i].Label));
                }
                else
                {
                    dataPoints00.Add(new DataPoint((double)((dataPoints0[i].Y - dataPoints[i].Y) * 100.0 / dataPoints[i].Y), dataPoints[i].Label));
                }
            }
            foreach (var i in dataPoints00)
            {
                if (i.Y == Double.PositiveInfinity)
                {
                    i.Y = 0;
                }
            }
            datafinal.DataPoints00 = JsonConvert.SerializeObject(dataPoints00, jsonSetting);
            datafinal.Chart1Height = 150 + dataPoints.Count() * 35;
            //
            //CHART2
            var doanhthuhangthang = pickthang.GroupBy(n => n.Thang).Select(cl => new DoanhHangThang { thang = cl.First().Thang, TongTien = cl.Sum(c => c.Tien) }).OrderBy(n => n.thang).ToList();
            var doanhthuhangthang1 = pickthang1.GroupBy(n => n.Thang).Select(cl => new DoanhHangThang { thang = cl.First().Thang, TongTien = cl.Sum(c => c.Tien) }).OrderBy(n => n.thang).ToList();
            List<DataPoint> dataPoints1 = new List<DataPoint>();
            List<DataPoint> dataPoints10 = new List<DataPoint>();
            foreach (var x in doanhthuhangthang)
            {
                dataPoints1.Add(new DataPoint(x.thang, (double)x.TongTien / 1000000000));
            }

            datafinal.DataPoints1 = JsonConvert.SerializeObject(dataPoints1, jsonSetting);
            foreach (var x in doanhthuhangthang1)
            {
                dataPoints10.Add(new DataPoint(x.thang, (double)x.TongTien / 1000000000));
            }
            datafinal.DataPoints10 = JsonConvert.SerializeObject(dataPoints10, jsonSetting);

