        void ConfigurnewConnectionString(string server, string database, string userid, string password, string ConnectionString)
        {
            string xxx = "\"";
            string str = "server=" + server + ";database=" + database + "; User ID=" + userid + "; Password=" + password + "";
            System.Configuration.Configuration Config1 = WebConfigurationManager.OpenWebConfiguration("~");
            ConnectionStringsSection conSetting = (ConnectionStringsSection)Config1.GetSection("connectionStrings");
            ConnectionStringSettings StringSettings = new ConnectionStringSettings(ConnectionString, "metadata=res://*/Models.KT_QN.csdl|res://*/Models.KT_QN.ssdl|res://*/Models.KT_QN.msl;provider=System.Data.SqlClient;provider connection string=" + xxx + "data source=" + server + ";initial catalog=" + database + ";persist security info=True;user id=" + userid + ";password=" + password + ";MultipleActiveResultSets=True;App=EntityFramework" + xxx, "System.Data.EntityClient");
            conSetting.ConnectionStrings.Remove(StringSettings);
            conSetting.ConnectionStrings.Add(StringSettings);
            Config1.Save(ConfigurationSaveMode.Modified);
        }
