            {
                return RedirectToAction("dang-xuat", "Login");
            }
            List<string> listcn = Info.macn.Split(',').ToList();
            var donvi = db2.TBL_DANHSACHCHINHANH.Where(n => listcn.Contains(n.macn) && n.check == true).ToList();
