            ViewBag.maplhd = DATATH1.TBL_DANHMUCPHANLOAIHD.OrderBy(n => n.TKHN == "632").ToList();
            ViewBag.otc = Info.phanloai.Split(',').ToList();
            ViewBag.READ_ONLY = Info.READ_ONLY;
            ViewBag.word = Info.WORD;
            ViewBag.excel = Info.EXCEL;
            ViewBag.pdf = Info.PDF;
            if (Info.manhom != null)
            {
                var listnhom = Info.manhom.Split(',').ToList();
                ViewBag.nhomhang = DATATH1.TBL_DANHMUCNHOMSANPHAM.Where(n => n.STT != 4 && n.STT != 0 && listnhom.Contains(n.MANHOM)).ToList();
            }
            else
            {
                ViewBag.nhomhang = DATATH1.TBL_DANHMUCNHOMSANPHAM.Where(n => n.STT != 4 && n.STT != 0).ToList();
            }
            var data = new BAOCAODATA() { CTHT = DATATH1.TBL_DANHMUCCHUONGTRINHHOTRO.OrderByDescending(n => n.ngayketthuc).Select(n => new BAOCAOCTHT { MACTHT = n.MACTHT, TENCTHT = n.TENCTHT, NGAYBATDAU = n.ngaybatdau, NGAYKETTHUC = n.ngayketthuc }).ToList(), LOAIBC = db2.TBL_DANHMUCLOAIBAOCAO.ToList() };
            if (Info.email == null)
            {
                ViewBag.email = true;
            }
            if (Info.khoatk == true)
            {
                return RedirectToAction("closetab");
            }
            if (Info.quyen.Split(',').Contains("CAP1"))
            {
                return View("BaoCaoThuGon", data);
            }
            else
            {
                return View("BaoCao", data);
            }
        }
