        [Authorize(Roles = "ADMIN")]
        [ActionName("lay-du-lieu-don-hang")]
        public ActionResult Laydulieudonhang(string tungay, string denngay)
        {
            //var dd = DateTime.Now.ToString("yyyyMMddHHmmss");
            var Info = GetInfo();
            ViewBag.dathang = Info.dathang;
            ViewBag.ten = Info.hoten;
            ViewBag.quyen = Info.quyen;

            DateTime tungay1 = DateTime.ParseExact(tungay, "dd/MM/yyyy", CultureInfo.InvariantCulture);
            DateTime denngay1 = DateTime.ParseExact(denngay, "dd/MM/yyyy", CultureInfo.InvariantCulture);
            var soso = "HN,PT,TB,TNG,HP,PTTT,CT,AG,CM,DN,LD,TG,TN,VL,BDG,BT,QN,TT423,BD,DNA,GL,HUE,NA,NT,THO".Split(',').ToList();
            var data = new List<DTA_DONDATHANG>();
            foreach (var i in soso)
            {
                if (queryCN.SingleOrDefault(n => n.macn == i) != null)
                {
                    data.AddRange(queryCN.SingleOrDefault(n => n.macn == i).data.DTA_DONDATHANG.Where(n => n.NgayDat >= tungay1 && n.NgayDat <= denngay1).ToList());
                }
                else if (queryCH.SingleOrDefault(n => n.macn == i) != null)
                {

                    data.AddRange(queryCH.SingleOrDefault(n => n.macn == i).data.DTA_DONDATHANG.Where(n => n.NgayDat >= tungay1 && n.NgayDat <= denngay1).ToList());
                }
            }
            PBIDATA.DTA_DONDATHANG.AddRange(data);
            PBIDATA.SaveChanges();
            return Content("<script type='text/javascript'>alert('Thành công'); window.close();</script>");
        }
