        [Authorize(Roles = "ADMIN")]
        [HttpPost]
        public ActionResult AddFile(string tenphanmem, string phienban, HttpPostedFileBase postedFile)
        {
            if (postedFile != null)
            {
                byte[] bytes;
                using (BinaryReader br = new BinaryReader(postedFile.InputStream))
                {
                    bytes = br.ReadBytes(postedFile.ContentLength);
                }
                var tv = db2.TBL_UPDATE_PROGRAM.SingleOrDefault(n => n.tenphanmem == tenphanmem);
                if (tv != null)
                {
                    tv.program = bytes;
                    tv.phienban = phienban;
                    tv.ngay = DateTime.Now;
                    db2.SaveChanges();
                }
                else
                {
                    db2.TBL_UPDATE_PROGRAM.Add(new TBL_UPDATE_PROGRAM() { ngay = DateTime.Now, phienban = phienban, program = bytes, tenphanmem = tenphanmem });
                    db2.SaveChanges();
                }
            }
