        [Authorize(Roles = "TONKHO")]
        [HttpPost]
        public ActionResult PartialTonKho(List<string> macn, string denngay, List<string> loaihang, string soluongton)
        {
            DateTime ngay = DateTime.ParseExact(denngay, "dd/MM/yyyy", CultureInfo.InvariantCulture);
            var data = new List<TTNHANHTONKHO>();
            var nhomhang = "";
            if (loaihang != null)
            {
                nhomhang = string.Join(",", loaihang);
            }

            data = DATATONKHO(macn, ngay, nhomhang, Sanitizer.GetSafeHtmlFragment(soluongton));

            if (soluongton != "")
            {
                var dieukien = Int32.Parse(soluongton.Remove(0, 1));
                switch (soluongton.Trim().Substring(0, 1))
                {
                    case ">":
                        data = data.Where(n => n.SL_TONCUOIKY2 > dieukien).ToList();
                        break;
                    case "<":
                        data = data.Where(n => n.SL_TONCUOIKY2 < dieukien).ToList();
                        break;
                    case "=":
                        data = data.Where(n => n.SL_TONCUOIKY2 == dieukien).ToList();
                        break;
                    case "#":
                        data = data.Where(n => n.SL_TONCUOIKY2 != dieukien).ToList();
                        break;
                    default:
                        break;
                }
            }
            return PartialView(data);
        }

