        {
            DateTime denngay1 = DateTime.ParseExact(denngay, "dd/MM/yyyy", CultureInfo.InvariantCulture);
            DateTime tungay1 = DateTime.ParseExact(tungay, "dd/MM/yyyy", CultureInfo.InvariantCulture);
            var str = "EXEC [sp_INSOCTIET_NGAY] '" + mahh + "','" + tungay1.ToString("MM/dd/yyyy") + "','" + denngay1.ToString("MM/dd/yyyy") + "'";
            var DATAX = new List<DATACHITIETHANGHOA>();
            foreach (var x in macn)
            {
                if (queryCN.SingleOrDefault(n => n.macn == x) != null)
                {
                    if (x == "SC")
                    {
                        str = str.Replace("sp_INSOCTIET_NGAY", "sp_INSOCTIET_NGAY_SOHDNB");
                    }
                    DATAX.AddRange(queryCN.SingleOrDefault(n => n.macn == x).data.Database.SqlQuery<DATACHITIETHANGHOA>(str).ToList());
                }
                else if (queryCH.SingleOrDefault(n => n.macn == x) != null)
                {
                    DATAX.AddRange(queryCH.SingleOrDefault(n => n.macn == x).data.Database.SqlQuery<DATACHITIETHANGHOA>(str).ToList());
                }
            }
            var data1 = new List<DATACHITIETHANGHOA1>();
            data1 = DATAX.OrderBy(n => n.NGAYLAPHD).Select(n => new DATACHITIETHANGHOA1 { DENNGAY = n.DENNGAY, DVT = n.DVT, HANDUNG = n.HANDUNG, KHANG = n.KHANG, MAHH = n.MAHH, MALO = n.MALO, NGAYLAPHD = (n.NGAYLAPHD != null) ? ((DateTime)n.NGAYLAPHD).ToString("dd/MM/yyyy") : "", SLMUA = n.SLMUA, SLTONDKY = n.SLTONDKY, SLXBAN = n.SLXBAN, SOHD = (n.SOHD == "" || n.SOHD == null) ? n.SOHD_NB : n.SOHD, TENHH = n.TENHH, TONCK = n.TONCK, TONGSLBAN = n.TONGSLBAN, TONGSLMUA = n.TONGSLMUA, TUNGAY = n.TUNGAY }).ToList();
