                rpt.Dispose();
                GC.Collect();
                return File(s, "application/vnd.ms-excel", "CHITIETHANGHOA.xls");
            }
            else
            {
                Stream s = rpt.ExportToStream(CrystalDecisions.Shared.ExportFormatType.PortableDocFormat);
