            var str = "EXEC laythongtintonkho_tatca_tatca  '" + loaihang + "','" + ngay.ToString("MM/dd/yyyy") + "'," + ngay.Month + "," + ngay.Year + ",''";

            List<TTNHANHTONKHO> DATAX = new List<TTNHANHTONKHO>();
            foreach (var x in soso)
            {
                if (queryCN.SingleOrDefault(n => n.macn == x) != null)
                {
                    var zz = queryCN.SingleOrDefault(n => n.macn == x).data.Database.SqlQuery<TTNHANHTONKHO>(str).ToList();
                    DATAX.AddRange(zz);
                }
                else if (queryCH.SingleOrDefault(n => n.macn == x) != null)
                {
                    var zz = queryCH.SingleOrDefault(n => n.macn == x).data.Database.SqlQuery<TTNHANHTONKHO>(str).ToList();
                    DATAX.AddRange(zz);
                }
            }
            return DATAX.ToList();
        }

        public DateTime GetFirstDay(int month, int year)
        {
            var firstDayOfMonth = new DateTime(year, month, 1);
            return firstDayOfMonth;
        }
        public DateTime GetLastDay(int month, int year)
        {
            var lastDayOfMonth = new DateTime(year, month, 1).AddMonths(1).AddDays(-1);
            return lastDayOfMonth;
        }
